import React, {useState} from 'react'
import L from 'leaflet';
import 'leaflet-geometryutil';
import 'leaflet/dist/leaflet.css';
import { MapContainer, TileLayer, Marker, Popup, Circle } from 'react-leaflet'
import iconShadow from 'leaflet/dist/images/marker-shadow.png';
import myIcon from '../images/shipColor.png'


let DefaultIcon = L.icon({
  iconSize: [26,36],
  iconAnchor: [13,36],
  iconUrl: '../images/shi.png',
  shadowUrl: iconShadow,
});

const shipIcon = new L.icon({
  iconUrl: myIcon,
  iconSize: [30, 30]
})
L.Marker.prototype.options.icon = DefaultIcon;


const Map = (props) => {

  const [distance, setDistance] = useState([]);
  const [range, setRange] = useState(0);

function getDistance(lat1, lon1, lat2, lon2){
  let R = 6371; 
  let dLat = toRad(lat2-lat1);
  let dLon = toRad(lon2-lon1); 
  let a = Math.sin(dLat/2) * Math.sin(dLat/2) +
          Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) * 
          Math.sin(dLon/2) * Math.sin(dLon/2); 
  let c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a)); 
  let d = R * c;
  return d;
}
function toRad(degree) {
  return degree*Math.PI/180;
}

function findBasesAtRisk() {
  console.log(distance);
  console.log(props.range);
  let bases = 0;
  for(let i = 0; i < distance.length; i++){
    if(parseFloat(props.range).toFixed(2) >= distance[i]){
      console.log(parseFloat(props.range).toFixed(2));
      bases = bases + 1;
    }
  }
  console.log(bases);
}

const handleRange = (e) => {
  setRange(e.target.value)
}
const handleReset = () => {
  setRange(0)
}

  const Ships = props.shipPositions.map(ship => (
    <Marker position={[ship.positionX, ship.positionY]} icon={shipIcon} eventHandlers={{
      click: (e) => {
        let distances = []
        for(let i = 0; i< props.basePositions.length ; i++){
          distances.push(getDistance(e.latlng.lat, e.latlng.lng, props.basePositions[i].positionX, props.basePositions[i].positionY));
          setDistance(distances);
        }
        findBasesAtRisk();            
      }
    }}>
      <Popup>
        <div>
          Nazwa: Lorem ipsum dolor sit amet consectetur adipisicing elit. Laborum, voluptate?
          <p>Zasięg: Lorem ipsum dolor sit amet.</p>
        </div>
        <Circle center={[ship.positionX, ship.positionY]} radius={props.range} pathOptions={{color: 'red'}}></Circle>
      </Popup>
    </Marker>
  )) 

  const Bases = props.basePositions.map(base => (
    <Marker position={[base.positionX, base.positionY]}>
    <Popup>
      <div>
          Military base
      </div>

    </Popup>
  </Marker>
  ))

  return (
<>
    <div className="col-md-9 map">
        <MapContainer center={[51.505, -0.09]} zoom={13} scrollWheelZoom={false}>
  <TileLayer
    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
  />
  {Ships}
  {Bases}
</MapContainer>
</div>
<div className="col-md-3">
            <form className="row g-3">
                <div className="col-md-9">

                    <select className="form-select">
                        <option value="0" selected>Wybierz rodzaj uzbrojenia</option>
                        <option value="100">One</option>
                        <option value="2240">Two</option>
                        <option value="108000">Three</option>
                    </select>
                    <div class="alert alert-secondary" role="alert">
                        Kliknij na statek aby zobaczyć możliwy zasięg
                    </div>
                    <button type="button" class="btn col-md-3 btn-outline-dark" onClick={handleReset}>Wyczyść</button>
                </div>

            </form>
</div>



 </>
  );

}

export default Map;
