import React, { useState, useEffect } from 'react'
import Map from './Map'

const Main = () => {

    const API_URL = "http://localhost:8080"

    const [range, setRange] = useState(0);
    const [ships, setShips] = useState([]);

    const shipPositions = [
        {positionX: 51.505, positionY: -0.09},
        {positionX: 52.505, positionY: -0.09},
        {positionX: 51.700, positionY: -0.09},
        {positionX: 50.505, positionY: -0.09},
    ]

    const basePositions = [
        {positionX: 51.505, positionY: -0.09},
        {positionX: 52.505, positionY: -0.09},
        {positionX: 51.700, positionY: -0.09},
        {positionX: 50.505, positionY: -0.09},
    ]
    const handleRange = (e) => {
        setRange(e.target.value)
    }
    const handleReset = () => {
        setRange(0)
    }
    useEffect(() => {
        fetch(API_URL+"/ships")
        .then(results => results.json())
        .then(data => {
            console.log(data);
            setShips(data)
        })
    }, [])

    return(
        <div className="row ">
            <div className="col-md-9 map">
                <Map shipPositions={shipPositions} range={range} basePositions={basePositions}></Map>
            </div>
            <div className="col-md-3">
                        <form className="row g-3">
                            <div className="col-md-9">

                                <select className="form-select" onChange={handleRange}>
                                    <option value="0" selected>Wybierz rodzaj uzbrojenia</option>
                                    <option value="100">One</option>
                                    <option value="2000">Two</option>
                                    <option value="30000">Three</option>
                                </select>
                                <div class="alert alert-secondary" role="alert">
                                    Kliknij na statek aby zobaczyć możliwy zasięg
                                </div>
                                <button type="button" class="btn col-md-3 btn-outline-dark" onClick={handleReset}>Wyczyść</button>
                            </div>

                        </form>
            </div>

        </div>
    )
}

export default Main;
