import React, {} from 'react'
import L from 'leaflet';
import 'leaflet-geometryutil';
import 'leaflet/dist/leaflet.css';
import { MapContainer, TileLayer, Marker, Popup, Circle } from 'react-leaflet'
import iconShadow from 'leaflet/dist/images/marker-shadow.png';
import myIcon from '../images/shipColor.png'

let DefaultIcon = L.icon({
  iconSize: [26,36],
  iconAnchor: [13,36],
  iconUrl: '../images/shi.png',
  shadowUrl: iconShadow,
});

const shipIcon = new L.icon({
  iconUrl: myIcon,
  iconSize: [30, 30]
})
L.Marker.prototype.options.icon = DefaultIcon;

function getDistance(lat1, lon1, lat2, lon2){
  let R = 6371; 
  let dLat = toRad(lat2-lat1);
  let dLon = toRad(lon2-lon1); 
  let a = Math.sin(dLat/2) * Math.sin(dLat/2) +
          Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) * 
          Math.sin(dLon/2) * Math.sin(dLon/2); 
  let c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a)); 
  let d = R * c;
  return d;
}
function toRad(degree) {
  return degree*Math.PI/180;
}

const Map = (props) => {

  const Ships = props.shipPositions.map(ship => (
    <Marker position={[ship.positionX, ship.positionY]} icon={shipIcon}>
      <Popup>
        <div>
          Nazwa: Lorem ipsum dolor sit amet consectetur adipisicing elit. Laborum, voluptate?
          <p>ZasiÄ™g: Lorem ipsum dolor sit amet.</p>
        </div>
        {console.log(getDistance(ship.positionX, ship.positionY, props.basePositions[0].positionX,props.basePositions[0].positionY))}
        <Circle center={[ship.positionX, ship.positionY]} radius={props.range} pathOptions={{color: 'red'}}></Circle>
      </Popup>
    </Marker>
  )) 

  const Bases = props.basePositions.map(base => (
    <Marker position={[base.positionX, base.positionY]}>
    <Popup>
      <div>
          Military base
      </div>

    </Popup>
  </Marker>
  ))

  return (
    <MapContainer center={[51.505, -0.09]} zoom={13} scrollWheelZoom={false}>
  <TileLayer
    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
  />
  {Ships}
  {Bases}
</MapContainer>
  );
}

export default Map;
