import React, { useState, useEffect } from 'react'
import Map from './Map'
import L from 'leaflet';
import 'leaflet-geometryutil';

const Main = () => {

    const API_URL = "http://localhost:8080"

    const [range, setRange] = useState(0);
    const [ships, setShips] = useState([]);
    const [bases, setBases] = useState([]);

    const shipPositions = [
        {positionX: 51.505, positionY: -0.09},
        {positionX: 52.505, positionY: -0.09},
        {positionX: 51.700, positionY: -0.09},
        {positionX: 50.505, positionY: -0.09},
    ]

    const basePositions = [
        {positionX: 51.525, positionY: -0.09},
        {positionX: 52.535, positionY: -2.09},
        {positionX: 51.705, positionY: -1.09},
        {positionX: 50.515, positionY: -0.09},
    ]
    const handleRange = (e) => {
        setRange(e.target.value)
    }
    const handleReset = () => {
        window.location.reload(false);
    }
    useEffect(() => {
        loadShips();
        loadBases();
    }, [])

    const loadShips = () => {
        fetch(API_URL+"/ships")
        .then(results => results.json())
        .then(data => {
            console.log(data);
            setShips(data)
        })
    }
    const loadBases = () => {
        fetch(API_URL+"/bases")
        .then(results => results.json())
        .then(data => {
            console.log(data);
            setBases(data)
        })
    }

    return(
        <div className="row ">
            <div className="col-md-9 map">
                <Map shipPositions={ships} range={range} basePositions={bases}></Map>
            </div>
            <div className="col-md-3">
                        <form className="row g-3">
                            <div className="col-md-9">

                                <select className="form-select" onChange={handleRange}>
                                    <option value="0" selected>Wybierz rodzaj uzbrojenia</option>
                                    <option value="100">One</option>
                                    <option value="2240">Two</option>
                                    <option value="108000">Three</option>
                                </select>
                                <div class="alert alert-secondary" role="alert">
                                    Kliknij na statek aby zobaczyć możliwy zasięg
                                    <p>Kliknij ponownie aby sprawdzić zagrożenia</p>
                                </div>
                                <button type="button" class="btn col-md-3 btn-outline-dark" onClick={handleReset}>Reset</button>
                                <button onClick={L.resetMap}>Reset</button>
                            </div>

                        </form>
            </div>

        </div>
    )
}

export default Main;
